SET SCHEMA EASYBUYDEV;
CREATE OR REPLACE TABLE "WeatherData_Daily"
( 
    "weatherid" INTEGER GENERATED BY DEFAULT AS IDENTITY,
    "app_max_temp" DECIMAL(5, 2),
    "app_min_temp" DECIMAL(5, 2),
    "clouds" INTEGER,
    "clouds_hi" INTEGER,
    "clouds_low" INTEGER,
    "clouds_mid" INTEGER,
    "datetime" VARCHAR(16),
    "dewpt" DECIMAL(5, 2),
    "high_temp" DECIMAL(5, 2),
    "low_temp" DECIMAL(5, 2),
    "max_dhi" DECIMAL(7, 2),
    "max_temp" DECIMAL(5, 2),
    "min_temp" DECIMAL(5, 2),
    "moon_phase" DECIMAL(7, 6),
    "moon_phase_lunation" DECIMAL(3, 1),
    "moonrise_ts" BIGINT,
    "moonset_ts" BIGINT,
    "ozone" DECIMAL(6, 1),
    "pop" INTEGER,
    "precip" DECIMAL(5, 2),
    "pres" DECIMAL(6, 1),
    "rh" INTEGER,
    "slp" DECIMAL(9, 5),
    "snow" DECIMAL(5, 2),
    "snow_depth" DECIMAL(5, 2),
    "sunrise_ts" BIGINT,
    "sunset_ts" BIGINT,
    "temp" DECIMAL(5, 2),
    "ts" BIGINT,
    "uv" DECIMAL(7, 6),
    "valid_date" VARCHAR(16),
    "vis" DECIMAL(5, 2),
    "weather_description" VARCHAR(100),
    "weather_code" INTEGER,
    "weather_icon" VARCHAR(10),
    "wind_cdir" CHAR(2),
    "wind_cdir_full" VARCHAR(10),
    "wind_dir" INTEGER,
    "wind_gust_spd" DECIMAL(7, 6),
    "wind_spd" DECIMAL(7, 6)
);

LABEL ON TABLE "WeatherData_Daily"
   IS 'Weather Data - Daily';

LABEL ON COLUMN "WeatherData_Daily" (
    "weatherid" TEXT IS 'Weather ID',
    "app_max_temp" TEXT IS 'Apparent Max Temperature',
    "app_min_temp" TEXT IS 'Apparent Min Temperature',
    "clouds" TEXT IS 'Clouds',
    "clouds_hi" TEXT IS 'High Clouds',
    "clouds_low" TEXT IS 'Low Clouds',
    "clouds_mid" TEXT IS 'Mid Clouds',
    "datetime" TEXT IS 'Datetime',
    "dewpt" TEXT IS 'Dew Point',
    "high_temp" TEXT IS 'High Temperature',
    "low_temp" TEXT IS 'Low Temperature',
    "max_dhi" TEXT IS 'Max Diffuse Horizontal Irradiance',
    "max_temp" TEXT IS 'Max Temperature',
    "min_temp" TEXT IS 'Min Temperature',
    "moon_phase" TEXT IS 'Moon Phase',
    "moon_phase_lunation" TEXT IS 'Moon Phase Lunation',
    "moonrise_ts" TEXT IS 'Moonrise Timestamp',
    "moonset_ts" TEXT IS 'Moonset Timestamp',
    "ozone" TEXT IS 'Ozone',
    "pop" TEXT IS 'Probability of Precipitation',
    "precip" TEXT IS 'Precipitation',
    "pres" TEXT IS 'Pressure',
    "rh" TEXT IS 'Relative Humidity',
    "slp" TEXT IS 'Sea Level Pressure',
    "snow" TEXT IS 'Snow',
    "snow_depth" TEXT IS 'Snow Depth',
    "sunrise_ts" TEXT IS 'Sunrise Timestamp',
    "sunset_ts" TEXT IS 'Sunset Timestamp',
    "temp" TEXT IS 'Temperature',
    "ts" TEXT IS 'Timestamp',
    "uv" TEXT IS 'UV Index',
    "valid_date" TEXT IS 'Valid Date',
    "vis" TEXT IS 'Visibility',
    "weather_description" TEXT IS 'Weather Description',
    "weather_code" TEXT IS 'Weather Code',
    "weather_icon" TEXT IS 'Weather Icon',
    "wind_cdir" TEXT IS 'Wind Direction',
    "wind_cdir_full" TEXT IS 'Wind Direction Full',
    "wind_dir" TEXT IS 'Wind Direction',
    "wind_gust_spd" TEXT IS 'Wind Gust Speed',
    "wind_spd" TEXT IS 'Wind Speed'
);